						<div className='uppercasetext borderbottom__lightgray margin-top__15px marginbottom__5px'>
							Notes
						</div>
						<div  id='jobapp__notes'>
                            { this.state.jobapp_notes}
                        </div>

					</div>
				</div>
			</div>

		</>
